mapper.insert:
/** com.framework.app.mapper.sample.SampleMapper.saveSample **/
INSERT INTO SAMPLE (
    SAMPLE_GROUP
    , SAMPLE_NAME
    , REG_ID
    , REG_DT
    , UPD_ID
    , UPD_DT
) VALUES (
    #{sampleGroup}
    , #{sampleName}
    , #{userId}
    , NOW()
    , #{userId}
    , NOW()
);

mapper.update:
/** com.framework.app.mapper.sample.SampleMapper.updateSample **/
UPDATE SAMPLE SET
    SAMPLE_GROUP = #{sampleGroup}
    , SAMPLE_NAME = #{sampleName}
    , UPD_ID = #{userId}
    , UPD_DT = NOW()
WHERE SAMPLE_ID = #{sampleId}
